import React, {
    useEffect,
    useState,
    useReducer
} from 'react'
import Rest from '../rest'

const baseURL = 'https://mymoney-71935.firebaseio.com/'
const {
    useGet,
    usePost,
    useDelete
} = Rest(baseURL)

const Home = () => {
    const data = useGet('movimentacoes')


    return (
        <>
            {data.loading &&
                <div class="spinner-border text-info" role="status">
                    <span class="sr-only">Loading...</span>
                </div>
            }

            {!data.loading && <pre>{JSON.stringify(data)}</pre>}
        </>
    )
}

export default Home
